mapper.select:
SELECT account_id, password, name, gender, email, phone_no, address
FROM account
WHERE account_id = #{accountId}

SELECT password,email,phone_no,address
FROM account
WHERE account_id = #{accountId}

mapper.update:
UPDATE account
SET password = #{password}, email = #{email}, phone_no = #{phoneNo} , address = #{address}
WHERE account_id = #{accountId}

mapper.select:
